# coding:utf-8from appium import webdriverclass Swipe_Method():    def __init__(self,driver):        self.driver = driver        self.height = self.driver.get_windowsize()["height"]        self.width = self.driver.get_windowsize()["width"]    # 向上滑动屏幕    def swipe_up(self):        self.driver.swipe(self.width / 2, self.height * 3 / 4, self.width / 2, self.height * 1 / 4)    # 向下滑动屏幕    def swipe_down(self):        self.driver.swipe(self.width / 2, self.height * 1 / 4, self.width / 2, self.height * 3 / 4)    # 向左滑动屏幕    def swipe_left(self):        self.driver.swipe(self.width * 3 / 4, self.height / 2, self.width * 1 / 4, self.height / 2)    # 向右滑动屏幕    def swipe_right(self):        self.driver.swipe(self.width * 1 / 4, self.height / 2, self.width * 3 / 4, self.height / 2)